import React from 'react'
// import { Button } from 'react-bootstrap'
import "./upload.css"
import { useState } from 'react'
import axios from 'axios';
const Upload = () => {
  const [newUser, setNewAuthor] = useState(
    {
        name: '',
        img: '',
    }
);
const handleSubmit = (e) => {
  console.log("trying")
  e.preventDefault();
  const formData = new FormData();
  // formData.append('photo', newUser.photo);
  formData.append('img', newUser.img);
  formData.append('name', newUser.name);
  
  axios.post('http://localhost:5000/api/users/upload', formData)
       .then(res => {
          console.log(res);
       })
       .catch(err => {
          console.log(err);
       });
}
const handleChange = (e) => {
  console.log("try")
  setNewAuthor({...newUser, [e.target.name]: e.target.value});
}

const handlePhoto = (e) => {
  console.log("ing")
  setNewAuthor({...newUser, photo: e.target.files[0]});
}
  return (
    <form onSubmit={handleSubmit} encType='multipart/form-data'>
            <input 
                type="file" 
                accept=".png, .jpg, .jpeg"
                name="img"
                onChange={handlePhoto}
            />

            <input 
                type="text"
                placeholder="name"
                name="name"
                value={newUser.name}
                onChange={handleChange}
            />

            {/* <input 
                type="date"
                name="birthdate"
                value={newUser.date}
                onChange={handleChange}
            /> */}

            <input 
                type="submit"
            />
        </form>
  )
}

export default Upload
