import React from 'react'
// import { Button } from 'react-bootstrap'
import "./upload.css"
import { useState } from 'react'
import axios from 'axios';

const Upload = () => {
  // const [img,setimg]=useState(null);
  const [src,setsrc]=useState("");
  const [path,setpath]=useState(null);
  const [newUser, setNewAuthor] = useState(
    {
        name: '',
        img: '',
    }
);
function _arrayBufferToBase64( buffer ) {
  // console.log(buffer);
  var binary = '';
  const bytes = new Uint32Array(buffer);
  console.log(bytes);
  var len = bytes.byteLength;
  for (var i = 0; i < len; i++) {
      binary += String.fromCharCode( bytes[ i ] );
  }
  return window.btoa( binary );
}
const handleSubmit = async(e) => {
  console.log("trying")
  e.preventDefault();
  const formData = new FormData();
  // formData.append('photo', newUser.photo);
  formData.append('img', newUser.img);
  formData.append('name', newUser.name);
  // formData.append('img', newUser.name);
  try{
    const config={
      baseURL: "//localhost:5000",
      origin: true,
      withCredentials: false,
      headers:{
        "Content-type":"application/json"
      }
    }
  console.log("hh")
  const data=await axios.post('//localhost:5000/api/users/upload/', formData)
      console.log(data.data) 
      // setpath(new Buffer.from(data.data.img).toString("base64"))
      
      // setimg()
      console.log(data.data.img)
      const buffer=data.data.img;
      const ans=_arrayBufferToBase64(buffer)
      console.log(ans);
      setsrc('data:image/jpeg;base64,'+ans);
      console.log(src);
  }
      catch(e){
        console.log(e);
        throw new Error("something happended");
      }
}
const handleChange = (e) => {
  console.log("try")
  setNewAuthor({...newUser, [e.target.name]: e.target.value});
}

const handlePhoto = (e) => {
  console.log("ing")
  setNewAuthor({...newUser, img: e.target.files[0]});
  console.log(newUser);
}
  return (
    <div>
    <form onSubmit={handleSubmit} encType='multipart/form-data'>
            <input 
                type="file" 
                accept=".png, .jpg, .jpeg"
                name="img"
                onChange={handlePhoto}
            />

            <input 
                type="text"
                placeholder="name"
                name="name"
                value={newUser.name}
                onChange={handleChange}
            />

            {/* <input 
                type="date"
                name="birthdate"
                value={newUser.date}
                onChange={handleChange}
            /> */}

            <input 
                type="submit"
            />
        </form>
        <div className='main'></div>

      </div>
  )
}

export default Upload
