import React, { useEffect, useState,useCallback } from 'react'
import { Button } from 'react-bootstrap'
import "./explore.css"
import Thumb from '../../components/Thumb.js'
const Explore = () => {
  const [allpokemon,setAllpoke]=useState([]);
  const [loadmore,setLoad]=useState("https://pokeapi.co/api/v2/pokemon?limit=20")
  const getallpoke = useCallback(async () =>{
    const res= await fetch(loadmore);
    const data= await res.json()
    // console.log(data.results)
    setLoad(data.next)
    function createpoke (result){
      // console.log(result)
      result.forEach( async(pokemon) => {
        const res=await fetch(`https://pokeapi.co/api/v2/pokemon/${pokemon.name}`)     
        const data =await res.json()
        setAllpoke(currentList=>[...currentList,data])
        await allpokemon.sort((a,b)=> a.id-b.id)
        // console.log(allpokemon);
      });
    }
    console.log("maybe")
    console.log(allpokemon);
    createpoke(data.results)  // console.log(data.results[0]);
  });
  useEffect(()=>{
    console.log("here")
    getallpoke();
    //enlist
  },[])
  return (
    <div className='app-container'>
      <h1>Explore</h1>
      <div className='pokemon-container'>
        <div className='all-container'>
        {allpokemon.map( (pokemonStats, index) => 
            <Thumb
              key={index}
              id={pokemonStats.id}
              image={pokemonStats.sprites.other.dream_world.front_default}
              name={pokemonStats.name}
              type={pokemonStats.types[0].type.name}
            />)}
        </div>
      </div>
      <Button className='Load-more'>Load more</Button>
    </div>
  )
}

export default Explore
