import React from 'react'
// import { Button } from 'react-bootstrap'
import "./upload.css"
import { useState } from 'react'
import axios from 'axios';
const Upload = () => {
  const [path,setpath]=useState(" ");
  const [newUser, setNewAuthor] = useState(
    {
        name: '',
        img: '',
    }
);
const handleSubmit = async(e) => {
  console.log("trying")
  e.preventDefault();
  const formData = new FormData();
  // formData.append('photo', newUser.photo);
  formData.append('img', newUser.img);
  formData.append('name', newUser.name);
  // formData.append('img', newUser.name);
  try{
    const config={
      baseURL: "//localhost:5000",
      origin: true,
      withCredentials: false,
      headers:{
        "Content-type":"application/json"
      }
    }
  const data=await axios.post('/api/users/upload/', formData)
      console.log(data.data) 
      setpath(data.data.img)
      }
      catch(e){
        throw new Error("something happended");
      }
}
const handleChange = (e) => {
  console.log("try")
  setNewAuthor({...newUser, [e.target.name]: e.target.value});
}

const handlePhoto = (e) => {
  console.log("ing")
  setNewAuthor({...newUser, img: e.target.files[0]});
  console.log(newUser);
}
  return (
    <div>
    <form onSubmit={handleSubmit} encType='multipart/form-data'>
            <input 
                type="file" 
                accept=".png, .jpg, .jpeg"
                name="img"
                onChange={handlePhoto}
            />

            <input 
                type="text"
                placeholder="name"
                name="name"
                value={newUser.name}
                onChange={handleChange}
            />

            {/* <input 
                type="date"
                name="birthdate"
                value={newUser.date}
                onChange={handleChange}
            /> */}

            <input 
                type="submit"
            />
        </form>
        <div><img src={require(`../../../../${path}`)}></img></div>
      </div>
  )
}

export default Upload
