}

const handlePhoto = (e) => {
  console.log("ing")
  setNewAuthor({...newUser, img: e.target.files[0]});
  console.log(newUser);
}
  return (
    <div>
    <form onSubmit={handleSubmit} encType='multipart/form-data'>
            <input 
                type="file" 
                accept=".png, .jpg, .jpeg"
                name="img"
                onChange={handlePhoto}
            />

            <input 
                type="text"
                placeholder="name"
                name="name"
                value={newUser.name}
                onChange={handleChange}
            />

            {/* <input 
                type="date"
