                value={newUser.date}
                onChange={handleChange}
            /> */}

            <input 
                type="submit"
            />
        </form>
        <div >
            <h1>here</h1>
            <img src={require(`./${src}`)} alt='img'></img>
        </div>
        
      </div>
  )
}

export default Upload
