import React from 'react'
import "./upload.css"
import { useState } from 'react'
import axios from 'axios';

const Upload = () => {
  const [src,setsrc]=useState("img-1669022705303.png");
  const [path,setpath]=useState(null);
  const [newUser, setNewAuthor] = useState(
    {
        name: '',
        img: '',
    }
);
const handleSubmit = async(e) => {
  console.log("trying")
  e.preventDefault();
  const formData = new FormData();
  formData.append('img', newUser.img);
  formData.append('name', newUser.name);
  try{
    const config={
      baseURL: "//localhost:5000",
      origin: true,
      withCredentials: false,
      headers:{
        "Content-type":"application/json"
      }
    }
  console.log("hh")
  const data=await axios.post('//localhost:5000/api/users/upload/', formData)
      console.log(data.data)       
      setTimeout(() => {setsrc(data.data.img)}, 5000);
  }
      catch(e){
        console.log(e);
        throw new Error("something happended");
      }
}
const handleChange = (e) => {
  console.log("try")
  setNewAuthor({...newUser, [e.target.name]: e.target.value});
}

const handlePhoto = (e) => {
  console.log("ing")
  setNewAuthor({...newUser, img: e.target.files[0]});
  console.log(newUser);
}
  return (
    <div>
    <form onSubmit={handleSubmit} encType='multipart/form-data'>
            <input 
                type="file" 
                accept=".png, .jpg, .jpeg"
                name="img"
                onChange={handlePhoto}
            />
            <input 
                type="text"
                placeholder="name"
                name="name"
                value={newUser.name}
                onChange={handleChange}
            />
            <input 
                type="submit"
            />
        </form>
        <div>
            <img src={require(`./${src}`)}></img>
        </div>
      </div>
  )
}

export default Upload
