		parts
			.map(part => {
				propertyPath += getPropertyAccess(part);
				return `${propertyPath}${_}=${_}${propertyPath}${_}||${_}{}`;
			})
			.concat(`${propertyPath}${getPropertyAccess(last)}`)
			.join(`,${_}`) + `${_}=${_}${assignment}`;
